def __getitem__(self, n1, n2=None, n3=None):
               t1 = t2 = t3 = None
               nargs = 1; t1 = type(n1)
               if (n2 != None): nargs = 2; t2 = type(n2)
               if (n3 != None): nargs = 3; t3 = type(n3)

