programlisting.textual:
return element test-case {
         attribute id { 
           concat($lang, '.',
             if ($uritype) then
                concat(substring($uritype,1,3), '.',
                       $reltype, $abstype)
             else '')	
         },
         element stylesheet-link {
            attribute type {$lang},
            if ($uritype eq 'relative') then
               attribute uri {$reltype}
            else 
               attribute uri {$abstype}
         },
         <wf-document/>
       }
